import React from 'react';
import LoginButton from '../../LoginButton/LoginButton';
import LogoutButton from '../../LogoutButton/LogoutButton';
import './Header.css'
import { withRouter } from 'react-router-dom'; 

const Header = (props) => {
    let modalButton = !props.activeUser && LoginButton(props)
    const logoutBtn = LogoutButton(props)
    return (
        <div className="DI_header_main">
            <div className="DI_logo" onClick={()=>{
                props.history.push("/")
            }}>
                Decentralize.io
            </div>
            <nav className="DI_nav_main">
                <div className="DI_Setting_wrapper">
                    <i className="fa fa-cog DI_Settings_icon" onClick={()=>{
                        //TODO open settings
                    }} />
                </div>
                {
                !props.activeUser && 
                   modalButton
                }
                {props.activeUser &&
                    logoutBtn
                }
            </nav>
        </div>
    )
}

export default withRouter(Header);
