const Header = (props) => {
    let modalButton = !props.activeUser && this.renderModalButton(props)
    logoutBtn = 
    retrun (
        <div className="DI_header_main">
            <div className="DI_logo" onClick={()=>{
                props.history.push("/")
            }}>
                Decentralize.io
            </div>
            <nav className="DI_nav_main">
                <div>
                    <i className="fa fa-cog" onClick={()=>{
                        //TODO open settings
                    }} />
                </div>
                {props.activeUser && 
                   modalButton
                }
                {this.state.activeUser &&
                    <div>
                        {logoutBtn}
                    </div>
                }
            </nav>
        </div>
    )
}

const renderLogoutButton = (props) => {
    const { ual: { activeUser, activeAuthenticator, logout } } = props
    if (!!activeUser && !!activeAuthenticator) {
      return (
        <p className='ual-btn-wrapper'>
          <div variant='DI_logout' onClick={logout}>
            {'Logout'}
          </div>
        </p>
      )
    }
  }

export default Header;
