import React from 'react';
import './SideMenu.css'
import SideMenu from './SideMenu'
import LoginButton from '../../LoginButton/LoginButton';
import LogoutButton from '../../LogoutButton/LogoutButton';

const SideMenuLayout = (props) => {
    let modalButton = !props.activeUser && LoginButton(props)
    const logoutBtn = LogoutButton(props)
    console.log(props)
    return (
        <div className="DI_Flex_row">
            <SideMenu {...props} />
            <div className="DI_Side_Menu_Col2">
                <div className="DI_Login_Button_wrapper DI_Login_Side_MenuBtn">
                    <div style={{display:'flex'}}>
                        <div className="DI_Setting_wrapper">
                            <i className="fa fa-cog DI_Settings_icon" onClick={() => {
                                //TODO open settings
                            }} />
                        </div>
                        {
                            !props.activeUser &&
                            modalButton
                        }
                        {props.activeUser &&
                            logoutBtn
                        }
                    </div>
                </div>
                <div>
                    {props.children}
                </div>

            </div>


        </div>
    )
}

export default SideMenuLayout;
