import { JsonRpc } from 'eosjs'
import React, {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import Header from '../layouts/Header/header';
const ourNetwork = {
  chainId: "cf057bbfb72640471fd910bcb67639c22df9f92470936cddc1ade0e2f2e7dc4f",
  rpcEndpoints: [{ protocol: "https", host: "api.testnet.eos.io", port: "" }]
}
const contract_account = "cthqkrpvdwcm";
const defaultState = {
  activeUser: null,
  accountName: '',
};

class DecentralizeApp extends Component {

  constructor(props) {
    super(props)
    this.state = {
       ...defaultState,
      rpc: new JsonRpc(`${ourNetwork.rpcEndpoints[0].protocol}://${ourNetwork.rpcEndpoints[0].host}${ourNetwork.rpcEndpoints[0].port !== "" ? ":" : ""}${ourNetwork.rpcEndpoints[0].port}`),
    }
  }

 componentDidUpdate = () => {
    const { ual: { activeUser } } = this.props
    if (activeUser && !this.state.activeUser) {
      this.setState({ activeUser }, this.updateAccountName)
    } else if (!activeUser && this.state.activeUser) {
      this.setState(defaultState)
    }
  }

  updateAccountName = async () => {
    try {
      const accountName = await this.state.activeUser.getAccountName()
      this.setState({ accountName }, this.updateAccountBalance)
    } catch (e) {
      console.warn(e)
    }
  }

  render() {
    const { ual: { activeUser } } = this.props
    const { accountName } = this.state

    return (
      <div className="DecentralizeApp_main">
        <Header accountName={accountName} activeUser={activeUser} ual={this.props.ual}  />
      </div>
    )
  }
}

export default DecentralizeApp;
