import * as React from 'react';
import './CreateOrganisation.css'
import Loading from '../Loading/Loading';

class CreateOrganisation extends React.Component {
    constructor(props) {
        super(props)
        console.log(props)
        this.state = {
            step: props.activeUser ? 2 : 1,
            loading: false,
            shareHolderCount: 1,
            activeUser: null,
            accountName: props.activeUser && props.activeUser.accountName ? props.activeUser.accountName : '',
            shareHolders: [{ shareholderName: '', symbol: '', shareholderAccount: '', balance: '' }],
            selectedOrganisation: '',
            defaultOrganisation: 'Forest Fund',
            votingConfiguration: [
                { name: 'Support % (?)', values: ['10%'] },
                { name: 'Minimal Approval % (?)', values: ['10%'] },
                { name: 'Vote Duration', values: ['1 Day', '0 Hours', '0 Mins'] }],
            setUpData: ['Name', 'Voting', 'Shareholders']
        }
    }

    componentDidMount() {
    }

    componentDidUpdate() {
        if (this.props.activeUser && this.state.activeUser !== this.props.activeUser) {
            this.setState({ step: 2, activeUser: this.props.activeUser })
        }
    }

    createOrganisation = () => {
        this.setState({ step: 0, loading: true })
    }

    setOrganisation = () => {
        return (
            <div className="organisationHeading">
                <div className='card-heading'>Creating Organisation</div>
                <Loading />

            </div>
        )
    }

    selectOrganisation = () => {
        let defaultOrganisation = this.state.defaultOrganisation
        this.setState({ selectedOrganisation: defaultOrganisation })
    }

    next = () => {
        let step = this.state.step
        this.setState({ step: step + 1 })
    }

    addMoreShares = () => {
        let shareHolders = this.state.shareHolders
        shareHolders.push({ shareholderName: '', symbol: '', shareholderAccount: '', balance: '' })
        let shareHolderCount = this.state.shareHolderCount
        this.setState({ shareHolderCount: shareHolderCount + 1, shareHolders })
    }

    onShareNameChange = (event, value, contentType, i) => {
        let shareHolders = this.state.shareHolders
        shareHolders[i][contentType] = event.target.value
        this.setState({ shareHolders })
    }

    renderStepOne = () => {
        return <div>
            <div className='card-heading'>Connect Wallet</div>
            <div className='card-description'>To create an organisation you need to connect your EOS account, please click connect below and follow the instruction to connect.</div>
            <button className="btn" onClick={this.props.ual.showModal}><i className="fa fa-copy connectBtnIcon"></i>Connect</button>
            <div className="card-bottom">
                <div className="card-bottom-container">
                    <i className="fa fa-lock lock"></i><span className="card-bottom-container-heading">Create Wallet (Coming soon) </span>
                    <div className="card-bottom-container-summary">
                        We’re working on a simplfied sign up experience for none blockchain users, check back soon for an update.
                    </div>
                </div>
            </div>
        </div>
    }

    renderStepTwo = () => {
        let selectedOrganisation = this.state.selectedOrganisation
        return <div>
            <div className='card-heading'>Organisation Name</div>
            <div className='Organisations'>Name</div>
            <div className='organisations-TextBox' onClick={this.selectOrganisation}>{selectedOrganisation === '' ? 'Select Organisation Name…' : selectedOrganisation}</div>
            <button className="btn next-btn" onClick={this.next}>Next</button>
        </div>

    }

    renderStepThree = () => {
        let votingConfiguration = this.state.votingConfiguration
        return <div>
            <div className='card-heading'>Configure Voting</div>
            <div className='voting-subInfo'>Edit at a later date within voting settings.</div>
            {votingConfiguration.map((config, i) => {
                return (<span key={i}>
                    <div className="voting-txt">{config.name}</div>
                    <span className="voting-value-box">
                        {config.values.map((value, i) => { return (<span className="voting-value" key={i}>{value}</span>) })}
                    </span>
                </span>)
            })}
            <button className="btn next-btn voting-next-btn" onClick={this.next}>Next</button>
        </div>

    }

    shareHolderFormFromat = () => {
        let shareHoldersList = []
        for (let i = 0; i < this.state.shareHolderCount; i++) {
            shareHoldersList.push(
                <span key={i}>
                    <div className="flexContainer">
                        <div className='share-col-1'>Share Name</div>
                        <div className='share-col-2'>Symbol</div>
                    </div>
                    <div className="flexContainer">
                        <input type="text"
                            className='organisations-TextBox org-txtBox-col-1'
                            onChange={(event, newValue) => this.onShareNameChange(event, newValue, 'shareholderName', i)}
                            value={this.state.shareHolders[i].shareholderName}
                            placeholder={'Name your share'} />
                        <input type="text"
                            value={this.state.shareHolders[i].symbol}
                            className='organisations-TextBox org-txtBox-col-2'
                            onChange={(event, newValue) => this.onShareNameChange(event, newValue, 'symbol', i)}
                            placeholder={'eg:BTC'} />
                    </div>
                    <div className="flexContainer">
                        <div className='share-col-1'>Shareholders</div>
                        <div className='share-col-2'>Balances</div>
                    </div>
                    <div className="flexContainer">
                        <input type="text"
                            className='organisations-TextBox org-txtBox-col-1'
                            value={this.state.shareHolders[i].shareholderAccount}
                            onChange={(event, newValue) => this.onShareNameChange(event, newValue, 'shareholderAccount', i)}
                            placeholder={'Enter EOS Account Name'} />
                        <input type="text"
                            className='organisations-TextBox org-txtBox-col-2'
                            value={this.state.shareHolders[i].balance}
                            onChange={(event, newValue) => this.onShareNameChange(event, newValue, 'balance', i)}
                            placeholder={'-'} />
                    </div>
                </span>
            )
        }
        return shareHoldersList
    }

    renderStepFour = () => {
        return <div>
            <div className='card-heading'>Configure Shareholders</div>
            <div className='voting-subInfo'>Edit at a later date within shareholder settings.</div>
            {this.shareHolderFormFromat()}
            {/* {this.state.addMore === 1 && this.shareHolderFormFromat()} */}
            <button className="btn next-btn addMore-share-btn" onClick={this.addMoreShares}>Add More</button>
            <button className="btn next-btn voting-next-btn" onClick={this.next}>Next</button>
        </div>

    }

    renderStepFive = () => {
        let setUpData = this.state.setUpData
        return <div>
            <div className='card-heading'>Confirm Setup</div>
            <div className='voting-subInfo'>Check your set up & continue when you’re ready.</div>
            {setUpData.map((obj, i) => {
                return (<div className='organisations-TextBox confirmSetup' onClick={this.selectOrganisation} key={i}>{obj}
                    <span className="editSetup">Edit</span>
                </div>)
            })}
            <button className="btn next-btn setup-next-btn" onClick={this.createOrganisation}>Create Organisation</button>
        </div>

    }

    render() {
        let { step } = this.state
        return (
            <div className='cardContainer'>
                <div className="card">
                    {step !== 0 &&
                        <div className="item">
                            <span className="notify-badge">{step === 5 ? 'Review' : `Step ${step} of 4`}</span>
                        </div>}

                    <div className="Orgnisation-container">
                        {step === 1 && this.renderStepOne()}
                        {step === 2 && this.renderStepTwo()}
                        {step === 3 && this.renderStepThree()}
                        {step === 4 && this.renderStepFour()}
                        {step === 5 && this.renderStepFive()}
                        {step === 0 && this.setOrganisation()}
                    </div>

                </div>
            </div>
        )
    }
}

export default CreateOrganisation;
