import { JsonRpc } from 'eosjs'
import React, {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
const ourNetwork = {
  chainId: "cf057bbfb72640471fd910bcb67639c22df9f92470936cddc1ade0e2f2e7dc4f",
  rpcEndpoints: [{ protocol: "https", host: "api.testnet.eos.io", port: "" }]
}
const contract_account = "cthqkrpvdwcm";
const defaultState = {
  activeUser: null,
  accountName: '',
};

class DecentralizeApp extends Component {

  constructor(props) {
    super(props)
    this.state = {
       ...defaultState,
      rpc: new JsonRpc(`${ourNetwork.rpcEndpoints[0].protocol}://${ourNetwork.rpcEndpoints[0].host}${ourNetwork.rpcEndpoints[0].port !== "" ? ":" : ""}${ourNetwork.rpcEndpoints[0].port}`),
    }
  }

 componentDidUpdate = () => {
    const { ual: { activeUser } } = this.props
    if (activeUser && !this.state.activeUser) {
      this.setState({ activeUser }, this.updateAccountName)
      this.getOrderData();
    } else if (!activeUser && this.state.activeUser) {
      this.setState(defaultState)
    }
  }

  updateAccountName = async () => {
    try {
      const accountName = await this.state.activeUser.getAccountName()
      this.setState({ accountName }, this.updateAccountBalance)
    } catch (e) {
      console.warn(e)
    }
  }

  renderLogoutButton = () => {
    const { ual: { activeUser, activeAuthenticator, logout } } = this.props
    if (!!activeUser && !!activeAuthenticator) {
      return (
        <p className='ual-btn-wrapper'>
          <div variant='outline-danger' onClick={logout}>
            {'Logout'}
          </div>
        </p>
      )
    }
  }

  renderModalButton=()=> {
    return (
      <p className='ual-btn-wrapper'>
        <div variant='outline-primary'
          onClick={this.props.ual.showModal}
          className='ual-generic-button'>Connect to Wallet</div>
      </p>
    )
  }

  render() {
    const { ual: { activeUser } } = this.props
    const { accountName } = this.state
    let modalButton = !activeUser && this.renderModalButton(),
    logoutBtn = this.renderLogoutButton(),
    loggedIn = accountName ? `Logged in as ${accountName}` : ''

    return (
      <div style={{ textAlign: 'center', paddingTop: '50px' }}>
        <h2>Decentralize.io</h2>
        {modalButton}
        <h3 className='ual-subtitle'>{loggedIn}</h3>
        { this.state.activeUser &&
          <div>
              {logoutBtn}
          </div>
        }
      </div>
    )
  }
}

export default DecentralizeApp;
