import React, { Component } from 'react'
import FinancialsPage from '../Financials/FinancialsPage'
import './Shareholder.css'
// const menuList = [{ mainMenu: 'Shareholeders', subMenu: [] }]

class Shareholder extends Component {
    constructor(props) {
        super(props)
        this.state = {
            activeSubMenu: 0,
            sideMenu: [
                { mainMenu: 'Shareholeders', subMenu: [{ name: 'Holders', live: true }, { name: 'Statistics', live: false }] },
                { mainMenu: 'Financials', subMenu: [{ name: 'Balances', live: true }] },
                { mainMenu: 'Voting', subMenu: [{ name: 'Token Balance', live: true }] }
            ],
            upcommingFeautre: ['Pay Roll', 'Boards', 'Social', 'Fundraising', 'Permissions', 'App Center'],
        }
    }

    componentDidMount() {
        this.state.sideMenu.map((menu, i) => {
            if (menu.mainMenu === this.props.match.params.menu) {
                this.setState({ activeSubMenu: i })
            }
            return menu;
        })
    }

    componentDidUpdate() {
        this.state.sideMenu.map((menu, i) => {
            if (menu.mainMenu === this.props.match.params.menu) {
                if (this.state.activeSubMenu !== i)
                    this.setState({ activeSubMenu: i })
            }
            return menu;
        })
    }

    renderSubMenu = () => {
        let { sideMenu, activeSubMenu } = this.state
        let activeSideMenu = sideMenu.filter(menu => menu.mainMenu === this.props.match.params.menu)
        return (
            <div className="DI_Organisation_MainHeader DI_Organisation_SubHeader">
                {
                    activeSideMenu.length > 0 &&
                    activeSideMenu[0].subMenu.map((menu, i) => {
                        return (
                            <div key={i} className="" style={{ display: 'flex' }}>
                                <div
                                    className={`DI_Organisation_SubHeader_Text ${(activeSubMenu === '' && i === 0) || (activeSubMenu === i) ? 'DI_Organisation_SubHeader_Text_Active' : ''} `}>
                                    {!menu.live && <i className="fa fa-lock" style={{ color: 'white', marginRight: "10px" }}></i>}{menu.name} {!menu.live && <span style={{ fontWeight: 100, marginLeft: "10px" }}>(Comming soon)</span>}</div>
                            </div>)
                    })
                }
            </div>
        )
    }


    render() {
        let { sideMenu, activeSubMenu } = this.state
        if (sideMenu[activeSubMenu].mainMenu === "Voting") {
            return (
                <div className="DI_FinancialsPage_main">
                    <div className="DI_Organisation_Main" >
                        <div className="DI_Organisation_MainHeader">
                            <div className="DI_Organisation_Title" >Voting</div>
                            <div className="DI_Edit_Organisation">New Proposal</div>
                        </div>
                        {this.renderSubMenu()}
                    </div>
                    <div className="DI_Org_voting_main">{"Transfers"}</div>
                </div>
            )
        } else if (sideMenu[activeSubMenu].mainMenu === "Financials") {
            return (
                <div className="DI_FinancialsPage_main">
                    <div className="DI_Organisation_Main" >
                        <div className="DI_Organisation_MainHeader">
                            <div className="DI_Organisation_Title" >Financials</div>
                            <div className="DI_Edit_Organisation">New Transfer</div>
                        </div>
                        {this.renderSubMenu()}
                    </div>
                    <FinancialsPage />
                </div>
            )
        } else {
            return (
                <div>
                    <div className="DI_Organisation_Main" >
                        <div className="DI_Organisation_MainHeader">
                            <div className="DI_Organisation_Title" >Shareholders</div>
                            <div className="DI_Edit_Organisation">Edit</div>
                        </div>
                        {this.renderSubMenu()}
                    </div>
                    <div className="DI_Table">
                        <table border="0" cellPadding="0" cellSpacing="0" >
                            <tr style={{}}>
                                <th style={{ width: '80%', paddingTop: '18px' }}>AccountName</th>
                                <th style={{ width: '20%', paddingTop: '18px' }}>Balance</th>
                                <th style={{ width: '10%', paddingTop: '18px' }}></th>
                            </tr>
                            <tr>
                                <td><hr /></td>
                                <td><hr /></td>
                                <td><hr /></td>
                            </tr>
                            <tr>
                                <td>40</td>
                                <td>Dean</td>
                            </tr>
                            <tr>
                                <td><hr /></td>
                                <td><hr /></td>
                                <td><hr /></td>
                            </tr>

                        </table>
                    </div>

                </div>
            )
        }
    }
}

export default Shareholder;
