import { JsonRpc } from 'eosjs'
import React, {Component} from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import HomePage from '../Pages/HomePage/HomePage';
import CreateOrganisation from '../Pages/CreateOrganisation/CreateOrganisation';
import ScrollToTop from '../ScrollToTop/ScrollToTop';
import HeaderLayout from '../layouts/Header/HeaderLayout';
import {  REACT_APP_EOSIO_ACCOUNT,REACT_APP_EOSIO_HOST, REACT_APP_EOSIO_CHAIN_ID, REACT_APP_EOSIO_PROTOCOL } from "../../common/config";


const ourNetwork = {
  chainId: REACT_APP_EOSIO_CHAIN_ID,
  rpcEndpoints: [{ protocol: REACT_APP_EOSIO_PROTOCOL, host: REACT_APP_EOSIO_HOST, port: "" }]
}
const accountName = REACT_APP_EOSIO_ACCOUNT;
const defaultState = {
  activeUser: null,
  accountName: '',
};

class DecentralizeApp extends Component {

  constructor(props) {
    super(props)
    this.state = {
       ...defaultState,
      rpc: new JsonRpc(`${ourNetwork.rpcEndpoints[0].protocol}://${ourNetwork.rpcEndpoints[0].host}${ourNetwork.rpcEndpoints[0].port !== "" ? ":" : ""}${ourNetwork.rpcEndpoints[0].port}`),
    }
  }

 componentDidUpdate = () => {
    const { ual: { activeUser } } = this.props
    if (activeUser && !this.state.activeUser) {
      this.setState({ activeUser }, this.updateAccountName)
    } else if (!activeUser && this.state.activeUser) {
      this.setState(defaultState)
    }
  }

  updateAccountName = async () => {
    try {
      const accountName = await this.state.activeUser.getAccountName()
      this.setState({ accountName }, this.updateAccountBalance)
    } catch (e) {
      console.warn(e)
    }
  }

  render() {
    const { ual: { activeUser } } = this.props
    const { accountName } = this.state
    let propsData = {
      accountName, activeUser, ual: this.props.ual
    }
    return (
      <div className="DecentralizeApp_main">
        <Router >
          <ScrollToTop>
            <Switch>
              <Route exact path="/" render={() => <HeaderLayout {...propsData} ><HomePage/></HeaderLayout>}/>
              <Route exact path="/CreateOrganisation"
               render={() => <HeaderLayout {...propsData} ><CreateOrganisation activeUser={activeUser} ual={this.props.ual}  accountName={accountName} />
               </HeaderLayout> } />
            </Switch>
          </ScrollToTop>
        {/* </SourceTracking> */}
        </Router>
      </div>
    )
  }
}

export default DecentralizeApp;
