import React, { Component } from 'react'
import FinancialsPage from '../Financials/FinancialsPage'
import Shareholder from './Shareholders/Shareholder';
import './Organisation.css'

class Organisation extends Component {
    constructor(props) {
        super(props)
        this.state = {
            activeSubMenu: 0,
            ShareholderList: [
                { account: 'Adamsmith123', balance: 40 },
                { account: 'Alexbeeston7', balance: 40 },
                { account: 'Callumhd2020', balance: 40 },
                { account: 'John Fisher', balance: 40 },
                { account: 'Chris Bidton', balance: 40 }],
            sideMenu: [
                { mainMenu: 'Shareholeders', headerBtnName: 'Edit', subMenu: [{ name: 'Holders', live: true }, { name: 'Statistics', live: false }] },
                { mainMenu: 'Financials', headerBtnName: 'New Transfer', subMenu: [{ name: 'Balances', live: true }] },
                { mainMenu: 'Voting', headerBtnName: 'New Proposal', subMenu: [{ name: 'Token Balance', live: true }] }
            ],
            upcommingFeautre: ['Pay Roll', 'Boards', 'Social', 'Fundraising', 'Permissions', 'App Center'],
        }
    }

    componentDidMount() {
        this.state.sideMenu.map((menu, i) => {
            if (menu.mainMenu === this.props.match.params.menu) {
                this.setState({ activeSubMenu: i })
            }
            return menu;
        })
    }

    componentDidUpdate() {
        console.log("componentDidUpdate....", this.props.match.params.menu)
        this.state.sideMenu.map((menu, i) => {
            if (menu.mainMenu === this.props.match.params.menu) {
                if (this.state.activeSubMenu !== i)
                    this.setState({ activeSubMenu: i })
            }
            return menu;
        })
    }

    renderSubMenu = () => {
        let { sideMenu, activeSubMenu } = this.state
        let activeSideMenu = sideMenu.filter(menu => menu.mainMenu === this.props.match.params.menu)
        return (
            <div className="DI_Organisation_MainHeader DI_Organisation_SubHeader">
                {
                    activeSideMenu.length > 0 &&
                    activeSideMenu[0].subMenu.map((menu, i) => {
                        return (
                            <div key={i} className="" style={{ display: 'flex' }}>
                                <div
                                    className={`DI_Organisation_SubHeader_Text ${(activeSubMenu === '' && i === 0) || (activeSubMenu === i) ? 'DI_Organisation_SubHeader_Text_Active' : ''} `}>
                                    {!menu.live && <i className="fa fa-lock" style={{ color: 'white', marginRight: "10px" }}></i>}{menu.name} {!menu.live && <span style={{ fontWeight: 100, marginLeft: "10px" }}>(Comming soon)</span>}</div>
                            </div>)
                    })
                }
            </div>
        )
    }


    render() {
        let { sideMenu, activeSubMenu, ShareholderList } = this.state
        console.log("activeSubMenu.............", activeSubMenu)
        return (
            <div>
                {

                    sideMenu.map((menu) => {
                        return (
                            sideMenu[activeSubMenu].mainMenu === menu.mainMenu &&
                            <div>
                                <div className="DI_Organisation_Main" >
                                    <div className="DI_Organisation_MainHeader">
                                        <div className="DI_Organisation_Title" >{menu.mainMenu}</div>
                                        <div className="DI_Edit_Organisation">{menu.headerBtnName}</div>
                                    </div>
                                    {this.renderSubMenu()}
                                </div>
                                {activeSubMenu === 0 && <Shareholder ShareholderList={ShareholderList} />}
                                {activeSubMenu === 2 && <div className="DI_Org_voting_main">{"Transfers"}</div>}
                                {activeSubMenu === 1 && <FinancialsPage />}


                            </div>
                        )
                    })
                }

            </div>
        )

    }
}

export default Organisation;
